import { useState, useEffect } from "react";
import { useRouter } from 'next/router';
import Link from "next/link";
import Header_css from "../component/Header_css";
import axios from "axios";

const Header = () => {

    const router = useRouter();
    const [userData, getData] = useState(null);
    const [logStat, getStat] = useState(false);

    useEffect(() => {

            console.log(router.pathname);
            console.log(localStorage.getItem('usrID'));
            getData(localStorage.getItem('usrID'));
            console.log(userData);

            if(localStorage.getItem('usrID') !== null) {
                getStat(true);
                console.log(logStat);
            }
            else{
                getStat(false);
                console.log(logStat);
                localStorage.clear();
            }

        const handleRouteChange = url => {
            console.log('App is changing to: ', url)
        }

        router.events.on('routeChangeStart', handleRouteChange)
        return () => {
            router.events.off('routeChangeStart', handleRouteChange)
        }

    },[logStat])

    function getMethod (e) {
        const val = e.target.name;
        router.push({ pathname: val });
    }

    function getMethodM (e) {
        const val = e.target.name;
        if (logStat) {
            router.push({ pathname: val });
        }
        else {
            alert('로그인이 필요한 서비스 입니다');
            router.push('/Login');
        }
    }

    function mLogout(e) {
        const val = e.target.name;
        alert('로그아웃 되었습니다!');
        localStorage.clear();
        router.push({ pathname: '/' });
    }

    return (
        <div class="body">
            <div class="main">
                <Header_css />
                <div class="main_image">
                    <div class="snsIcons">
                        <a href="" target="_blank">
                            <img
                                class="icon"
                                src= "/facebook.png"
                                height="15px"
                            />
                        </a>
                        <a href="" target="_blank">
                            <img
                                class="icon"
                                src="/instagram.png"
                                height="15px"
                            />
                        </a>
                        <a href="" target="_blank">
                            <img
                                class="icon"
                                src="/twitter.png"
                                height="15px"
                            />
                        </a>
                    </div>
                    <div class="logo">
                        <img class="mainLogo" src="/mainLogo.png" />
                        <div class="thisiscinema">
                            <img
                                src="/this_is_cinema.png"
                                height="50px"
                            />
                        </div>
                    </div>
                    <nav class="menubarNav">
                        <ul class="menubar">
                            <li>
                                <a name = "/index" onClick={getMethod}> 메인</a>
                            </li>
                            <li>
                                <a  name = "/reservation" onClick={getMethod}>영화예매</a>
                            </li>
                            <li>
                                {logStat ? <a name='/index' onClick={mLogout}>로그아웃</a> : <a name='/Login' onClick={getMethod}>로그인</a>}
                            </li>
                            {logStat ? <></> : <li>
                                <a name="/Join" onClick={getMethod}>회원가입</a>
                            </li> }
                            <li>
                                <a name='/Mypage' onClick={getMethodM}> 마이페이지 </a>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        </div>
    );
};

export default Header;
